Insert into DF.MOVIE (MOVIE_NUMBER,MOVIE_CD,MOVIE_TITLE,MOVIE_SUMMARY,MOVIE_RELEASE_DATE,MOVIE_RUNTIME,MOVIE_PRICE,MOVIE_PREVIEW,MOVIE_RATING,MOVIE_MAIN_POSTER,GENRE_NUMBER,DIRECTOR_NUMBER) values (126,'20210397','가가린','가가린 옥상에서 하늘을 보며 우주비행사를 꿈꾸던 10대 소년, 유리./r/n사랑하는 사람들과 함께 꿈을 키운 공간이었던 가가린 주택단지의 철거가 결정되고,/r/n유리는 가가린을 지키기 위해 고군분투한다./r/n유리는 자신의 우상이자 우주, 그리고 집이었던 가가린 주택단지를 지켜낼 수 있을까?',to_date('22/12/22','RR/MM/DD'),'97',10000,'https://www.youtube.com/embed/z43aooiVU4s','12세이상관람가','https://www.kobis.or.kr/common/mast/movie/2022/11/c36543e82f0b49818d87e276ea2ed806.jpg',1,107);
