Insert into DF.MOVIE (MOVIE_NUMBER,MOVIE_CD,MOVIE_TITLE,MOVIE_SUMMARY,MOVIE_RELEASE_DATE,MOVIE_RUNTIME,MOVIE_PRICE,MOVIE_PREVIEW,MOVIE_RATING,MOVIE_MAIN_POSTER,GENRE_NUMBER,DIRECTOR_NUMBER) values (144,'20211062','판문점','1951년 7월, 한국전쟁 당사자들은 휴전을 모색한다. 휴전을 위해, 연합군과 공산군 양측은 개성 동북쪽에 위치한 내봉장에서 협상을 시작한다. 그러나 서로 날카롭게 대립할 뿐, 협상은 나아가지 못한다. 1951년 10월이 되면서 양측은 개성 아래에 있는 널문리 작은 마을에서 다시 만난다. 그들은 그곳에 협상을 위한 천막을 세우고, <판문점>이란 이름을 붙인다. 널문리를 뜻하는 ‘판문’과 주막의 ‘점’을 합한 이름, <판문점>은 그렇게 탄생했다.',null,'78',10000,'https://www.youtube.com/embed/50nAU-WFthk',null,null,6,123);
Insert into DF.MOVIE (MOVIE_NUMBER,MOVIE_CD,MOVIE_TITLE,MOVIE_SUMMARY,MOVIE_RELEASE_DATE,MOVIE_RUNTIME,MOVIE_PRICE,MOVIE_PREVIEW,MOVIE_RATING,MOVIE_MAIN_POSTER,GENRE_NUMBER,DIRECTOR_NUMBER) values (145,'20211101','절해고도','“돌아갈 수 있는 길이야. 막힌 길도 아니고.”/r/n/r/n촉망받는 조각가였던 윤철은 아내와 이혼 후/r/n지방 소도시에서 무엇이든 납품하는 인테리어 업자로 살고 있다./r/n윤철에게는 지나라는 딸이 있는데 그녀는 아빠를 닮아 미술에 재능을 보인다./r/n어느날 지나의 고등학교에서 윤철에게 호출이 오고/r/n기괴하고 어두운 그림을 아무데나 그리며 문제아로 낙인 찍힌 지나는/r/n미대 진학을 포기한 채 갑작스레 출가를 선언한다./r/n윤철 또한 바람처럼 자신에게 불어온 여자 영지를 만나 예측하지 못한 사랑에 빠진다./r/n/r/n생의 길목에서 한 선택들은 우리를 어디로 데려갈까./r/n그리고 우리는 어디에서 다시 만나게 될까./r/n절망의 앞에서 도망치지 않았던 당신에게 도착한 마음의 필적/r/n인생이라는 협곡, 나라는 절망, 다시 여기라는 절경 ‘절해고도’',to_date('23/09/27','RR/MM/DD'),'109',10000,'https://www.youtube.com/embed/D3m0ditXt5E','12세이상관람가','https://www.kobis.or.kr/common/mast/movie/2023/08/b736bc8211274477835e53e5053f26b8.jpg',7,124);
Insert into DF.MOVIE (MOVIE_NUMBER,MOVIE_CD,MOVIE_TITLE,MOVIE_SUMMARY,MOVIE_RELEASE_DATE,MOVIE_RUNTIME,MOVIE_PRICE,MOVIE_PREVIEW,MOVIE_RATING,MOVIE_MAIN_POSTER,GENRE_NUMBER,DIRECTOR_NUMBER) values (146,'20211242','고속도로 가족','“지갑을 잃어버려서 그러는데, 2만 원만 빌려주시겠어요?”/r/n고속도로 휴게소에서 텐트를 집, 밤하늘의 달을 조명 삼아 살고 있는 기우(정일우)와 가족들./r/n다시 마주칠 일 없는 휴게소 방문객들에게 돈을 빌려 캠핑하듯 유랑하며 살아가던 이들이/r/n어느 날, 이미 한 번 만난 적 있는 영선(라미란)과 다른 휴게소에서 다시 마주친다./r/n/r/n인생은 놀이, 삶은 여행처럼 살아가던 고속도로 가족과 그들이 신경 쓰이는 영선./r/n이 두 번의 우연한 만남은 예기치 못한 사건으로 이어지는데...',to_date('22/11/02','RR/MM/DD'),'128',10000,'https://www.youtube.com/embed/gjxsA47TfaA','15세이상관람가','https://www.kobis.or.kr/common/mast/movie/2022/10/fc97e5a3466d4528af518852a5cdca41.jpg',7,125);
