Insert into DF.MOVIE (MOVIE_NUMBER,MOVIE_CD,MOVIE_TITLE,MOVIE_SUMMARY,MOVIE_RELEASE_DATE,MOVIE_RUNTIME,MOVIE_PRICE,MOVIE_PREVIEW,MOVIE_RATING,MOVIE_MAIN_POSTER,GENRE_NUMBER,DIRECTOR_NUMBER) values (1424,'20231576','삼각형의 마음','배우들로만 이루어진 산악회인 ‘맘 산악회’는 18년째 매주 북한산에만 오르고 있다. 극장이 쉬는 월요일에 혼자 있지 말자며 배우 손병호를 중심으로 모이기 시작해, 산악회를 오고 간 배우가 백여 명이 넘는다. 모두가 다른 위치에서, 각자의 마음에 아픔과 꿈과 목표를 품고 함께 서울을 지키고 있는 큰 산을 오른다.',null,'90',10000,'https://www.youtube.com/embed/hdz_HfJHIXY',null,'https://www.kobis.or.kr/common/mast/movie/2023/04/45a80f5e38b542ada1e8cfed1ffc2cab.png',6,null);
Insert into DF.MOVIE (MOVIE_NUMBER,MOVIE_CD,MOVIE_TITLE,MOVIE_SUMMARY,MOVIE_RELEASE_DATE,MOVIE_RUNTIME,MOVIE_PRICE,MOVIE_PREVIEW,MOVIE_RATING,MOVIE_MAIN_POSTER,GENRE_NUMBER,DIRECTOR_NUMBER) values (1425,'20231577','라방','“내 여자친구가 인터넷 몰카 라이브 방송에 나온다?!”/r/n/r/n대기업 취업도 쉽지 않고 친구가 보낸 불법 몰카 라이브 방송 링크를/r/n여자친구 수진에게 들켜 헤어질 위기에 처한 프리랜서 PD ‘동주’./r/n수진의 생일날 이벤트를 준비하던 중 의문의 라이브 방송 링크가 전송되고 링크가 자동으로 열리는데…/r/n/r/n“아이디 486번 고객님이 입장되었습니다”/r/n/r/n실시간으로 시작되는 몰카 라이브 방송!/r/n그런데 모니터를 통해 여자친구 ‘수진’이 나타나고 동주는 충격에 빠지게 된다./r/n정체불명의 ‘젠틀맨’과 정신을 잃은 ‘수진’,/r/n‘동주’는 사랑하는 수진을 구하기 위해 라이브 방송에 참여하기 시작하지만/r/n‘젠틀맨’은 라이브 방송을 점점 최악의 상황으로 끌고 가는데…/r/n/r/n1분 1초를 다투는 숨 막히는 실시간 라이브 추격전이 시작된다!',to_date('23/06/28','RR/MM/DD'),'90',10000,'https://www.youtube.com/embed/iE7kLHDMboY','청소년관람불가','https://www.kobis.or.kr/common/mast/movie/2023/05/16e6e5794ba8420ab0d2d29a7a962969.jpg',14,887);
